import React, { useState } from "react";
import axios from "axios";

const ImageCaptioning = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [caption, setCaption] = useState("");
  const [loading, setLoading] = useState(false);

  // Handle image selection
  const handleImageChange = (e) => {
    setSelectedImage(e.target.files[0]);
  };

  // Handle image submission to FastAPI
  const handleSubmit = async () => {
    if (!selectedImage) return;

    setLoading(true);
    const formData = new FormData();
    formData.append("file", selectedImage);

    try {
      const response = await axios.post("http://localhost:8000/generate-caption/", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      // Handle the caption returned from FastAPI
      if (response.data.caption) {
        setCaption(response.data.caption);
      } else {
        setCaption("No caption returned.");
      }
    } catch (error) {
      console.error("Error uploading image:", error);
      setCaption("Error generating caption.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>Image Captioning with VLLM</h1>
      <input type="file" accept="image/*" onChange={handleImageChange} />
      <button onClick={handleSubmit} disabled={loading}>
        {loading ? "Generating Caption..." : "Generate Caption"}
      </button>

      {caption && (
        <div>
          <h2>Generated Caption:</h2>
          <p>{caption}</p>
        </div>
      )}
    </div>
  );
};

export default ImageCaptioning;
